	function AmIFollowingHim($uid){
		$CI =& get_instance();
		return $AmIFanOfHim = $CI->DatabaseModel->select_data('fan_id','become_a_fan use INDEX(user_id,following_id)',array('user_id'=>$uid,'following_id'=>is_login()));
				
	}
	
	function CanIFollowHim($uid){
		
		if($uid !== is_login()){
			$other 	= WhoAmI($uid);
			$self 	= WhoAmI(is_login());
			
			$case = (int) $self.$other;
			return follow_fan_access_level($case);
		}
		return 'NO';
	}
	
	function WhoAmI($uid){  /* Means user main category*/
		$CI =& get_instance();
		$accessParam = array(
							'field' => 'users.user_level',
							'where' => 'user_id='.$uid,
							);
		$user = $CI->query_builder->user_list($accessParam);
		if(isset($user['users'][0]['user_level']))
		return $CI->query_builder->user_list($accessParam)['users'][0]['user_level'];
	}
	
	function follow_fan_access_level($case){
		switch ($case) {
			case 11: 	/*'ICON TO ICON'*/
				return  'YES';
				break;
			case 12:	/*'ICON TO EMRGING'*/
				return  'NO';
				break;
			case 13:	/*'ICON TO BRAND'*/
				return  'YES';
				break;
			case 14:	/*'ICON TO FAN'*/
				return  'NO';
				break;
			case 21:	/*'EMRGING TO ICON  '*/
				return  'YES';
				break;
			case 22:	/*'EMRGING TO EMRGING  '*/
				return  'NO';
				break;	
			case 23:	/*'EMRGING TO BRAND  '*/
				return  'YES';
				break;	
			case 24:	/*'EMRGING TO FAN  '*/
				return  'NO';
				break;	
			case 31:	/*'BRAND TO ICON  '*/
				return  'YES';
				break;	
			case 32:	/*'BRAND TO EMRGING  '*/
				return  'YES';
				break;	
			case 33:	/*'BRAND TO BRAND  '*/
				return  'YES';
				break;	
			case 34:	/*'BRAND TO FAN  '*/
				return  'NO';
				break;	
			case 41:	/*'FAN TO ICON  '*/
				return  'YES';
				break;
			case 42:	/*'FAN TO EMRGING  '*/
				return  'YES';
				break;
			case 43:	/*'FAN TO BRAND  '*/
				return  'YES';
				break;
			default:	/*'FAN TO FAN  '*/
				return  'NO';
				break;
		}
	}
	
	
