	/*
		START OF ENDORSEMENT BUTTON
	
	*/
	
	function EndorseButton($uid){
		$CI =& get_instance();
		$button = '';
		if(!empty(is_login())){
			if(CanIEndorseHim($uid) == 'YES'){
				$u = !empty(is_login())?$uid:''; 
				
				$AmIEndorseHim = AmIEndorsingHim($uid);
				$endorse = 'Endorse Me';
				$status = 0;
				if(isset($AmIEndorseHim[0]['endorsement_status'])){
					if($AmIEndorseHim[0]['endorsement_status'] == 1){
						$endorse = 'Endorsement Requested';
						$status = 1;
					}else if($AmIEndorseHim[0]['endorsement_status'] == 2){
						$endorse = 'Endorsement Accepted';
						$status = 2;
					}else if($AmIEndorseHim[0]['endorsement_status'] == 3){
						$endorse = 'Endorsement Rejected';
						$status = 3;
					}
				}
				$button = '<a href="javascript:;" class="dis_btn endorse_you" data-uid="'.$u.'" data-status="'.$status.'">'.$endorse.'</a>';
			}
		}
		return $button;			
	}
	function AmIEndorsingHim($uid){
	
	$CI =& get_instance();
	return $CI->DatabaseModel->select_data('endorsement_status','become_a_endorser',array('endorsee_id'=>$uid,'endorser_id'=>is_login()));
				
	}
	function CanIEndorseHim($uid){
		
		if($uid !== is_login()){
			$other 	= WhoAmI($uid);
			$self 	= WhoAmI(is_login());
			
			$case = (int) $self.$other;
			return follow_endorse_access_level($case);
		}
		return 'NO';
	}
	
	function follow_endorse_access_level($case){
		switch ($case) {
			case 11: 	/*'ICON TO ICON'*/
				return  'YES';
				break;
			case 12:	/*'ICON TO EMRGING'*/
				return  'YES';
				break;
			case 13:	/*'ICON TO BRAND'*/
				return  'YES';
				break;
		
			case 31:	/*'BRAND TO ICON  '*/
				return  'YES';
				break;	
			case 32:	/*'BRAND TO EMRGING  '*/
				return  'YES';
				break;	
			case 33:	/*'BRAND TO BRAND  '*/
				return  'YES';
				break;	
			default:	
				return  'NO';
				break;
		}
	}
	
